function EditCoopSchedule()
{
    const navigate = useNavigate();
    const {schedule_id,weekday,subject} = useParams();
    const[inputWeekday,setInputWeekday]=useState("");
    const[inputSubject,setInputSubject]=useState("");

    return(
        <div>
            <h1>Edit Your Coop Schedule</h1>
            Weekday :<input type="text" value={weekday} onChange={e=>setInputWeekday(e.target.value)}></input><br></br>
            Subject :<input type="text" value={subject} onChange={e=>setInputSubject(e.target.value)}></input><br></br>

            <button onClick={() => edit(item.id,item.weekday,item.subject)}>Save</button>
            <button onClick={() => edit(item.id,item.weekday,item.subject)}>Cancel</button>
        </div>
    )
}

export default EditCoopSchedule;
