import React,{useState,useEffect} from "react";
import axios, { Axios } from "axios";
import { useParams } from "react-router-dom";
//const id='';

function CoopSchedule()
{
    const[errorMessage,setErrorMessage]=useState("");
    const[schedule,setSchedule]=useState("");

    const CoopSchedule = () => 
    {
        let id = useParams();
        return(id);
    };

    const get_coop_schedule = async(e) => {
        //e.preventDefault();
        const data = CoopSchedule();
        console.log(data);
        await axios.post('http://localhost:8080/student_coop_schedule',data )
            .then(res=>
            {
                console.log(res.data);
                setSchedule(res.data);
            })
            .catch(error => {
                setErrorMessage("No Co-op Schedule found!");
            })
    }

    const get_coop_subject_name = async(e) => {
        //e.preventDefault();
        const data = CoopSchedule();
        console.log(data);
        await axios.post('http://localhost:8080/coop_subject_name',data )
            .then(res=>
            {
                console.log(res.data);
                setSchedule(res.data);
            })
            .catch(error => {
                setErrorMessage("No Co-op Schedule found!");
            })
    }

    get_coop_schedule();

    return(
        <div>
            <h1>Your Coop Schedule</h1>
            {schedule?(
                <table>
                    <thead>
                        <tr>
                            <th>Weekday</th>
                            <th>Subject</th>
                            <th></th>
                        </tr>
                    </thead>
                    <tbody>
                        {schedule.map(item=>(
                            <tr>
                            <td>{item.weekday}</td>
                            <td>{item.subject_id}</td>
                            <td></td>
                        </tr>
                        ))}
                    </tbody>
                </table>
            ):
            (
                <p>{errorMessage}</p>
            )}   
        </div>
    )
}

export default CoopSchedule;
