import React,{useState,useEffect} from "react";
import axios, { Axios } from "axios";
import { useNavigate } from "react-router-dom";
import { useParams } from "react-router-dom";
//const id='';

function CoopSchedule()
{
    const[errorMessage,setErrorMessage]=useState("");
    const[schedule,setSchedule]=useState("");
    const navigate = useNavigate();

    const CoopSchedule = () => 
    {
        let id = useParams();
        return(id);
    };

    const get_coop_schedule = async(e) => {
        //e.preventDefault();
        const data = CoopSchedule();
        //console.log(data);
        await axios.post('http://localhost:8080/student_coop_schedule',data )
            .then(res=>
            {
                //console.log(res.data);
                setSchedule(res.data);
            })
            .catch(error => {
                setErrorMessage("No Co-op Schedule found!");
            })
    }

    const edit = (schedule_id) => {
       //e.preventDefault();
         navigate("/editcoopschedule/"+schedule_id)
    }

    get_coop_schedule();

    return(
        <div>
            <h1>Your Coop Schedule</h1>
            {schedule?(
                <table>
                    <thead>
                        <tr>
                            <th>Weekday</th>
                            <th>Subject</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {schedule.map(item=>(
                        <tr>
                            <td>{item.weekday}</td>
                            <td>{item.subject}</td>
                            <td><button type="submit" onClick={edit(item.id)}>Edit</button></td>
                        </tr>
                        ))}
                    </tbody>
                </table>
            ):
            (
                <p>{errorMessage}</p>
            )}   
        </div>
    )
}

export default CoopSchedule;
