import React,{useState,useEffect} from "react";
import axios, { Axios } from "axios";
import { useNavigate } from "react-router-dom";
import { useParams } from "react-router-dom";
import './Style.css';
//const id='';

function CoopSchedule()
{
    const[errorMessage,setErrorMessage]=useState("");
    const[schedule,setSchedule]=useState("");
    const navigate = useNavigate();
    const {id} = useParams();

    const CoopSchedule = () => 
    {
        let id = useParams();
        return(id);
    };

    const get_coop_schedule = async(e) => {
        //e.preventDefault();
        const data = CoopSchedule();
        //console.log(data);
        await axios.post('http://localhost:8080/student_coop_schedule',data )
            .then(res=>
            {
                //console.log(res.data);
                setSchedule(res.data);
            })
            .catch(error => {
                setErrorMessage("No Co-op Schedule found!");
            })
    }

    const edit = (schedule_id,weekday,subject) => {
    //    e.preventDefault();
       // const data = CoopSchedule();
        navigate("/editcoopschedule/"+id+"/"+schedule_id+"/"+weekday+"/"+subject)
        //console.log("ready")

    }

    get_coop_schedule();

    return(
        <div class="myschedule">
            <h1>My Coop Schedule</h1>
            {schedule?(
                <table>
                    <thead>
                        <tr>
                            <th>WEEDKAY</th>
                            <th>SUBJECT</th>
                            <th>ACTION</th>
                        </tr>
                    </thead>
                    <tbody>
                        {schedule.map(item=>(
                        <tr>
                            <td>{item.weekday}</td>
                            <td>{item.subject}</td>
                            <td><button type="submit" onClick={() => edit(item.id,item.weekday,item.subject)} style="margin-right:10px">Edit</button>
                                <button type="submit">Delete</button>
                            </td>
                        </tr>
                        ))}
                    </tbody>
                </table>
            ):
            (
                <p>{errorMessage}</p>
            )}   
        </div>
    )
}

export default CoopSchedule;
