import React,{useState,useEffect} from "react";
import axios, { Axios } from "axios";
import { useNavigate } from "react-router-dom";

function LoginForm()
{
    const[loginUsername,setLoginUsername]=useState("");
    const[loginPassword,setLoginPassword]=useState("");
    const[errorMessage,setErrorMessage]=useState("");
    
    const login = async (e) => {
        e.preventDefault();
        // This where we would have our post method
        //console.log({ loginUsername, loginPassword })
        const data = { loginUsername, loginPassword }
        await axios.post('http://localhost:8080/login', data)
            .then(res=>
            {
                if(res.status===200)
                {
                    console.log(res);
                }
                else
                {
                    setErrorMessage("Login unsuccessful. Try again!");
                }
            }
            )
            .catch(error => {
                setErrorMessage("Login unsuccessful. Try again!");
            })
    }

    return(
        <div>
            <h1>Sign in</h1>
            <label>Username</label><input onChange={e=>setLoginUsername(e.target.value)} required/>
            <label>Password</label><input onChange={e=>setLoginPassword(e.target.value)} required/>
            <button type="submit" onClick={login}>Sign in</button>
            <p>{errorMessage}}</p>
        </div>
    )
}

export default LoginForm;
