import React,{useState,useEffect} from "react";
import axios, { Axios } from "axios";

function LoginForm()
{
    const[loginUsername,setLoginUsername]=useState("");
    const[loginPassword,setLoginPassword]=useState("");

    const login=(loginUsername,loginPassword)=>
    {
        new Axios({
        method:"POST",
        data:
        {
            username:loginUsername,
            password:loginPassword,
        },
        withCredentials:true,
        URL:"http://localhost:3000/login",
        })
        .then(function(res)
        {
            console.log(res);
        });
    };

    return(
        <div>
            <h1>Sign in</h1>
            <label>Username</label><input onChange={e=>setLoginUsername(e.target.value)} required/>
            <label>Password</label><input onChange={e=>setLoginPassword(e.target.value)} required/>
            <button type="submit" onClick={login(loginUsername,loginPassword)}>Sign in</button>
        </div>
    )
}

export default LoginForm;
